import React, { useState } from "react";
import Header from "./Header";
import TopBrandRestaurants from "./TopBrandRestaurants";
import "./Searchpage.css"; // Import the CSS file

const RestaurantSearch = () => {
  const [locationFilter, setLocationFilter] = useState("");
  const [restaurantFilter, setRestaurantFilter] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  const restaurants = [
    {
      name: "Restaurant A",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 8.99,
          cuisine: "Indian",
        },
        {
          name: "Item 2A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 9.99,
          cuisine: "Indian",
        },
        {
          name: "Item 3A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 7.99,
          cuisine: "Indian",
        },
      ],
    },
    {
      name: "Restaurant B",
      location: "Location 2",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 8.99,
          cuisine: "Indian",
        },
        {
          name: "Item 2A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 9.99,
          cuisine: "Indian",
        },
        {
          name: "Item 3A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 7.99,
          cuisine: "Indian",
        },
      ],
    },
    {
      name: "Restaurant C",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 8.99,
          cuisine: "Indian",
        },
        {
          name: "Item 2A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 9.99,
          cuisine: "Indian",
        },
        {
          name: "Item 3A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 7.99,
          cuisine: "Indian",
        },
      ],
    },
  ];


  const handleLocationFilterChange = (event) => {
    setLocationFilter(event.target.value);
  };

  const handleRestaurantFilterChange = (event) => {
    setRestaurantFilter(event.target.value);
  };

  const addToCart = (item) => {
    console.log("Added to cart:", item);
  };

  const handleSearchClick = () => {
    const filteredRestaurants = restaurants.filter((restaurant) => {
      const locationMatch = restaurant.location
        .toLowerCase()
        .includes(locationFilter.toLowerCase());
      const restaurantMatch = restaurant.name
        .toLowerCase()
        .includes(restaurantFilter.toLowerCase());
      return locationMatch && restaurantMatch;
    });
    setFilteredRestaurants(filteredRestaurants);
    setSelectedRestaurant(null);
    setSearchClicked(true);
  };

  const handleRestaurantSelection = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  const renderMenuItem = (item) => (
    <div key={item.name} className="search-item-card">
      <h5>{item.name}</h5>
      <img src={item.image} alt={item.name} />
      <p>Cuisine: {item.cuisine}</p>
      <p>Price: ${item.price.toFixed(2)}</p>
      <button onClick={() => addToCart(item)}>Add to Cart</button>
    </div>
  );

  const renderRestaurants = (restaurantsToRender) => (
    <div className="restaurant-list">
      {restaurantsToRender.map((restaurant) => (
        <div
          key={restaurant.name}
          className="search-restaurant-card"
          onClick={() => handleRestaurantSelection(restaurant)}
        >
          <img src={restaurant.image} alt={restaurant.name} />
          <div>
            <h3>{restaurant.name}</h3>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div>
      <div>
        <Header />
      </div>
      <div className="search-bar">
        <label>
          <input
            type="text"
            placeholder="Search by location"
            value={locationFilter}
            onChange={handleLocationFilterChange}
          />
        </label>
        <label>
          <input
            type="text"
            placeholder="Search by restaurant"
            value={restaurantFilter}
            onChange={handleRestaurantFilterChange}
          />
        </label>
        <button className="search-button" onClick={handleSearchClick}>
          Search
        </button>
      </div>
      <div className="content">
        {selectedRestaurant ? (
          <div>
            <h3>Selected Restaurant: {selectedRestaurant.name}</h3>
           
            <h4>Menu Items</h4>
            {selectedRestaurant.menuItems.length === 0 ? (
              <p>No menu items found.</p>
            ) : (
              <div>{selectedRestaurant.menuItems.map(renderMenuItem)}</div>
            )}
          </div>
        ) : (
          <div>
            {searchClicked ? (
              filteredRestaurants.length === 0 ? (
                <p>No restaurants found.</p>
              ) : (
                renderRestaurants(filteredRestaurants)
              )
            ) : (
              <TopBrandRestaurants />
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantSearch;
