import React, { useState } from 'react';
import Header from './Header';
import TopBrandRestaurants from './TopBrandRestaurants';

// Sample data for restaurants and menu items
const restaurants = [
  {
    name: 'Restaurant A',
    location: 'Location 1',
    menuItems: [
      { name: 'Item 1', cuisine: 'Cuisine A', image: 'image1.jpg', price: '$10' },
      { name: 'Item 2', cuisine: 'Cuisine B', image: 'image2.jpg', price: '$12' },
      { name: 'Item 3', cuisine: 'Cuisine C', image: 'image3.jpg', price: '$15' },
    ],
  },
  {
    name: 'Restaurant B',
    location: 'Location 2',
    menuItems: [
      { name: 'Item 4', cuisine: 'Cuisine A', image: 'image4.jpg', price: '$9' },
      { name: 'Item 5', cuisine: 'Cuisine B', image: 'image5.jpg', price: '$11' },
      { name: 'Item 6', cuisine: 'Cuisine C', image: 'image6.jpg', price: '$14' },
    ],
  },
  {
    name: 'Restaurant C',
    location: 'Location 1',
    menuItems: [
      { name: 'Item 7', cuisine: 'Cuisine A', image: 'image7.jpg', price: '$8' },
      { name: 'Item 8', cuisine: 'Cuisine B', image: 'image8.jpg', price: '$13' },
      { name: 'Item 9', cuisine: 'Cuisine C', image: 'image9.jpg', price: '$16' },
    ],
  },
];

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('location');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = () => {
    if (selectedFilter === 'location') {
      const filteredRestaurants = restaurants.filter(
        (restaurant) => restaurant.location.toLowerCase() === searchTerm.toLowerCase()
      );
      setSearchResults(filteredRestaurants);
    } else {
      const filteredRestaurant = restaurants.find(
        (restaurant) => restaurant.name.toLowerCase() === searchTerm.toLowerCase()
      );
      setSearchResults(filteredRestaurant ? [filteredRestaurant] : []);
    }
  };

  const handleFilterChange = (event) => {
    setSelectedFilter(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div>
      <div>
        <Header />
      </div>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <div>
          <input
            type="text"
            value={searchTerm}
            onChange={(event) => setSearchTerm(event.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button onClick={handleSearch}>Search</button>
        </div>
        <div style={{ marginLeft: '10px' }}>
          <label>
            Filter by:
            <select value={selectedFilter} onChange={handleFilterChange}>
              <option value="location">Location</option>
              <option value="restaurant">Restaurant</option>
            </select>
          </label>
        </div>
      </div>
      {searchResults.length === 0 ? (
        <div>
          {searchTerm && <p>No results found.</p>}
          <TopBrandRestaurants />
        </div>
      ) : (
        <div>
          {searchResults.map((restaurant) => (
            <div key={restaurant.name}>
              <h3>{restaurant.name}</h3>
              <p>Location: {restaurant.location}</p>
              <h4>Menu:</h4>
              {restaurant.menuItems.map((menuItem, index) => (
                <div key={index}>
                  <h5>{menuItem.name}</h5>
                  <p>Cuisine: {menuItem.cuisine}</p>
                  <img src={menuItem.image} alt={menuItem.name} style={{ width: '200px' }} />
                  <p>Price: {menuItem.price}</p>
                </div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
