import React, { useState } from 'react';
import Header from './Header';
import './Searchpage.css';

const Searchpage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [filteredMenuItems, setFilteredMenuItems] = useState([]);

  // Dummy data for locations, restaurants, and menu items
  const locations = [
    { id: 1, name: 'Chennai' },
    { id: 2, name: 'Coimbatore' },
    { id: 3, name: 'Bangalore' },
  ];

  const restaurants = [
    { id: 1, name: 'Restaurant 1', locationId: 1 },
    { id: 2, name: 'Restaurant 2', locationId: 2 },
    { id: 3, name: 'Restaurant 3', locationId: 3 },
  ];

  const menuItems = [
    { id: 1, name: 'Item 1', restaurantId: 1 },
    { id: 2, name: 'Item 2', restaurantId: 1 },
    { id: 3, name: 'Item 3', restaurantId: 2 },
    { id: 4, name: 'Item 4', restaurantId: 2 },
    { id: 5, name: 'Item 5', restaurantId: 3 },
  ];

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    // Filter locations based on the search query
    const filteredLocations = locations.filter((location) =>
      location.name.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredLocations(filteredLocations);

    // Filter restaurants based on the search query
    const filteredRestaurants = restaurants.filter((restaurant) =>
      restaurant.name.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredRestaurants(filteredRestaurants);

    // Filter menu items based on the search query
    const filteredMenuItems = menuItems.filter((menuItem) =>
      menuItem.name.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredMenuItems(filteredMenuItems);
  };

  return (
    <div className="search-page">
      <div>
        <Header />
      </div>
      <div className="search-container">
        <input
          className="search-input"
          type="text"
          placeholder="Search locations or restaurants"
          value={searchQuery}
          onChange={handleSearch}
        />
        <div className="results-container">
          {filteredLocations.length > 0 && (
            <div className="location-results">
              <h2>Locations:</h2>
              <ul>
                {filteredLocations.map((location) => (
                  <li key={location.id}>{location.name}</li>
                ))}
              </ul>
            </div>
          )}
          {filteredRestaurants.length > 0 && (
            <div className="restaurant-results">
              <h2>Restaurants:</h2>
              <ul>
                {filteredRestaurants.map((restaurant) => (
                  <li key={restaurant.id}>{restaurant.name}</li>
                ))}
              </ul>
            </div>
          )}
          {filteredMenuItems.length > 0 && (
            <div className="menu-item-results">
              <h2>Menu Items:</h2>
              <ul>
                {filteredMenuItems.map((menuItem) => (
                  <li key={menuItem.id}>{menuItem.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Searchpage;
