import React, { useState } from 'react';
import Header from './Header';
import './Searchpage.css';

const Searchpage = () => {
  const [locationFilter, setLocationFilter] = useState('');
  const [restaurantFilter, setRestaurantFilter] = useState('');
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [filteredMenuItems, setFilteredMenuItems] = useState([]);

  // Dummy data for locations, restaurants, and menu items
  const locations = [
    { id: 1, name: 'Chennai' },
    { id: 2, name: 'Coimbatore' },
    { id: 3, name: 'Bangalore' },
  ];

  const restaurants = [
    { id: 1, name: 'Restaurant 1', locationId: 1 },
    { id: 2, name: 'Restaurant 2', locationId: 2 },
    { id: 3, name: 'Restaurant 3', locationId: 3 },
  ];

  const menuItems = [
    { id: 1, name: 'Item 1', restaurantId: 1 },
    { id: 2, name: 'Item 2', restaurantId: 1 },
    { id: 3, name: 'Item 3', restaurantId: 2 },
    { id: 4, name: 'Item 4', restaurantId: 2 },
    { id: 5, name: 'Item 5', restaurantId: 3 },
  ];

  const handleSearch = () => {
    let filteredRestaurants = restaurants;
    let filteredMenuItems = menuItems;

    if (locationFilter) {
      filteredRestaurants = restaurants.filter(
        (restaurant) => restaurant.locationId === parseInt(locationFilter)
      );
    }

    if (restaurantFilter) {
      filteredMenuItems = menuItems.filter(
        (menuItem) => menuItem.restaurantId === parseInt(restaurantFilter)
      );
    }

    setFilteredRestaurants(filteredRestaurants);
    setFilteredMenuItems(filteredMenuItems);
  };

  const getLocationName = (locationId) => {
    const location = locations.find((loc) => loc.id === parseInt(locationId));
    return location ? location.name : '';
  };

  return (
    <div className="search-page">
      <div>
        <Header />
      </div>
      <div className="search-container">
        <div className="filter-container">
          <input
            className="search-input"
            type="text"
            placeholder="Search locations"
            value={locationFilter}
            onChange={(event) => setLocationFilter(event.target.value)}
          />
          <input
            className="search-input"
            type="text"
            placeholder="Search restaurants"
            value={restaurantFilter}
            onChange={(event) => setRestaurantFilter(event.target.value)}
          />
          <button className="search-button" onClick={handleSearch}>
            Search
          </button>
        </div>
        <div className="results-container">
          {locationFilter && (
            <div className="location-results">
              <h2>Restaurants in {getLocationName(locationFilter)}:</h2>
              {filteredRestaurants.map((restaurant) => (
                <div key={restaurant.id}>
                  <h3>{restaurant.name}</h3>
                  <ul>
                    {filteredMenuItems
                      .filter((menuItem) => menuItem.restaurantId === restaurant.id)
                      .map((menuItem) => (
                        <li key={menuItem.id}>{menuItem.name}</li>
                      ))}
                  </ul>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Searchpage;
