import React, { useState, useRef } from 'react';
import Header from './Header';
import './Searchpage.css';

const Searchpage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // Dummy list of locations
  const locations = ['Chennai', 'Coimbatore', 'Bangalore', 'Delhi', 'Mumbai', 'Kolkata', 'Hyderabad', 'Pune', 'Ahmedabad'];

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    // Filter the locations based on the search query
    const filteredLocations = locations.filter((location) =>
      location.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(filteredLocations);
  };

  const suggestionsContainerRef = useRef(null);

  const scrollSuggestions = (scrollAmount) => {
    suggestionsContainerRef.current.scrollBy({ top: scrollAmount, behavior: 'smooth' });
  };

  return (
    <div className="search-page">
      <div>
        <Header />
      </div>
      <div className="search-container">
        <input
          className="search-location"
          type="text"
          placeholder="Search locations"
          value={searchQuery}
          onChange={handleSearch}
        />
        {searchQuery && (
          <div className="suggestions-container" ref={suggestionsContainerRef}>
            <ul className="suggestions">
              {searchResults.map((location) => (
                <li key={location}>{location}</li>
              ))}
            </ul>
            <div className="scroll-buttons">
              <button className="scroll-up" onClick={() => scrollSuggestions(-100)}>
                &#8679;
              </button>
              <button className="scroll-down" onClick={() => scrollSuggestions(100)}>
                &#8681;
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Searchpage;
