import React, { useState } from 'react';
import './ProfilePage.css';

const ProfilePage = () => {
  const [name, setName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [phoneNumberError, setPhoneNumberError] = useState('');
  const [changesSaved, setChangesSaved] = useState(false);

  const handleSaveChanges = () => {
    if (password !== confirmPassword) {
      setPasswordError('Please enter the correct password.');
      return;
    }

    if (phoneNumber.length !== 10) {
      setPhoneNumberError('Please enter a 10-digit phone number.');
      return;
    }

    // Perform the necessary actions to save the changes
    console.log('Name:', name);
    console.log('Phone Number:', phoneNumber);
    console.log('Address:', address);
    console.log('Password:', password);

    setChangesSaved(true);
    setTimeout(() => {
      setChangesSaved(false);
    }, 3000);
  };

  return (
    <div className="profile-page">
      <h2>Profile Page</h2>
      <label htmlFor="name">Name:</label>
      <input
        type="text"
        id="name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <label htmlFor="phoneNumber">Phone Number:</label>
      <input
        type="text"
        id="phoneNumber"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
      />

      {phoneNumberError && (
        <p className="error-message">{phoneNumberError}</p>
      )}

      <label htmlFor="address">Address:</label>
      <input
        type="text"
        id="address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
      />

      <label htmlFor="password">Change Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <label htmlFor="confirmPassword">Confirm Password:</label>
      <input
        type="password"
        id="confirmPassword"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
      />

      {passwordError && <p className="error-message">{passwordError}</p>}

      {changesSaved && (
        <p className="success-message">Changes saved successfully.</p>
      )}

      <button onClick={handleSaveChanges}>Save Changes</button>
    </div>
  );
};

export default ProfilePage;
