import React, { useState } from 'react';
import logo from './logo.jpeg'; // Replace with the path to your logo file
import './Header.css';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';

const Header = () => {
  const profilePicUrl = 'https://img.freepik.com/free-icon/user_318-875902.jpg'; // Replace with the URL of your profile picture
  const [showOptions, setShowOptions] = useState(false);

  const toggleOptions = () => {
    setShowOptions(!showOptions);
  };

  return (
    <div className="header">
      <img src={logo} alt="Logo" style={logoStyle} />
      <div className="profile-icon" onClick={toggleOptions}>
        <img src={profilePicUrl} alt="Profile" style={profilePicStyle} />
        {showOptions && (
          <div className="options-container">
            <div className="profile-options">
              <Router>
                <Routes>
                  <Route></Route>
                </Routes>
              </Router>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const logoStyle = {
  width: '75px',
  height: '75px',
  marginRight: '10px',
  border: '5px solid red',
 
};

const profilePicStyle = {
  width: '50px',
  height: '50px',
  borderRadius: '50%',
};

export default Header;
