import React, { useState } from "react";
import Header from "./Header";
import TopBrandRestaurants from "./TopBrandRestaurants";
import './Searchpage.css';

const RestaurantSearch = () => {
  const [locationFilter, setLocationFilter] = useState("");
  const [restaurantFilter, setRestaurantFilter] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  // Sample restaurant data
  const restaurants = [
    {
      name: "Restaurant A",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: ["Item 1A", "Item 2A", "Item 3A"]
    },
    {
      name: "Restaurant B",
      location: "Location 2",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: ["Item 1B", "Item 2B", "Item 3B"]
    },
    {
      name: "Restaurant C",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: ["Item 1C", "Item 2C", "Item 3C"]
    },
  ];

  // Handle location filter change
  const handleLocationFilterChange = (event) => {
    setLocationFilter(event.target.value);
  };

  // Handle restaurant filter change
  const handleRestaurantFilterChange = (event) => {
    setRestaurantFilter(event.target.value);
  };

  // Handle search button click
  const handleSearchClick = () => {
    const filteredRestaurants = restaurants.filter((restaurant) => {
      return (
        restaurant.location.toLowerCase().includes(locationFilter.toLowerCase()) &&
        restaurant.name.toLowerCase().includes(restaurantFilter.toLowerCase())
      );
    });
    setFilteredRestaurants(filteredRestaurants);
    setSelectedRestaurant(null); // Reset selected restaurant
    setSearchClicked(true); // Set search button clicked
  };

  // Handle restaurant selection
  const handleRestaurantSelection = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  // Render filtered restaurants
  let filteredRestaurantsContent = null;
  if (searchClicked) {
    if (selectedRestaurant) {
      filteredRestaurantsContent = (
        <div className="selected-restaurant">
          <h3>Selected Restaurant: {selectedRestaurant.name}</h3>
          <img src={selectedRestaurant.image} alt={selectedRestaurant.name} style={{ width: "200px" }} />
          {selectedRestaurant.menuItems.length === 0 ? (
            <p>No menu items found.</p>
          ) : (
            <>
              <h4>Menu Items</h4>
              <ul>
                {selectedRestaurant.menuItems.map((item) => (
                  <li key={item}>{item}</li>
                ))}
              </ul>
            </>
          )}
        </div>
      );
    } else if (filteredRestaurants.length === 0) {
      filteredRestaurantsContent = <p>No restaurants found.</p>;
    } else {
      filteredRestaurantsContent = (
        <ul>
          {filteredRestaurants.map((restaurant) => (
            <li
              key={restaurant.name}
              onClick={() => handleRestaurantSelection(restaurant)}
            >
              <div>
                <h3>{restaurant.name}</h3>
                <img src={restaurant.image} alt={restaurant.name} style={{ width: "100px" }} />
              </div>
            </li>
          ))}
        </ul>
      );
    }
  } else {
    filteredRestaurantsContent = <TopBrandRestaurants />;
  }

  return (
    <div>
      <div>
        <Header />
      </div>
      {/* Location and restaurant search */}
      <div style={{ display: "flex", alignItems: "center" }}>
        <label>
          Location:
          <input type="text" value={locationFilter} onChange={handleLocationFilterChange} />
        </label>
        <label style={{ marginLeft: "10px" }}>
          Restaurant:
          <input type="text" value={restaurantFilter} onChange={handleRestaurantFilterChange} />
        </label>
        <button onClick={handleSearchClick}>Search</button>
      </div>

      {/* Display filtered restaurants or selected restaurant items */}
      {selectedRestaurant ? (
        <div className="selected-restaurant">
          <h3>Selected Restaurant: {selectedRestaurant.name}</h3>
          <img src={selectedRestaurant.image} alt={selectedRestaurant.name} style={{ width: "200px" }} />
          {selectedRestaurant.menuItems.length === 0 ? (
            <p>No menu items found.</p>
          ) : (
            <div>
              <h4>Menu Items</h4>
              <ul>
                {selectedRestaurant.menuItems.map((item) => (
                  <li key={item}>{item}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      ) : (
        <div>
          {/* Display filtered restaurants */}
          {filteredRestaurantsContent}
        </div>
      )}
    </div>
  );
};

export default RestaurantSearch;
