import React, { useState } from 'react';
import Header from './Header';
import './Searchpage.css';

const Searchpage = () => {
  const [locationFilter, setLocationFilter] = useState('');
  const [restaurantFilter, setRestaurantFilter] = useState('');
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [filteredMenuItems, setFilteredMenuItems] = useState([]);

  // Dummy data for locations, restaurants, and menu items
  const locations = [
    { id: 1, name: 'Chennai' },
    { id: 2, name: 'Coimbatore' },
    { id: 3, name: 'Bangalore' },
  ];

  const restaurants = [
    { id: 1, name: 'Restaurant 1', locationId: 1 },
    { id: 2, name: 'Restaurant 2', locationId: 1 },
    { id: 3, name: 'Restaurant 3', locationId: 3 },
  ];

  const menuItems = [
    { id: 1, name: 'Item 1', restaurantId: 1 },
    { id: 2, name: 'Item 2', restaurantId: 1 },
    { id: 3, name: 'Item 3', restaurantId: 2 },
    { id: 4, name: 'Item 4', restaurantId: 2 },
    { id: 5, name: 'Item 5', restaurantId: 3 },
  ];

  const handleSearch = () => {
    let filteredLocations = locations;
    let filteredRestaurants = restaurants;
    let filteredMenuItems = menuItems;

    // Filter locations based on the location filter
    if (locationFilter) {
      filteredLocations = locations.filter((location) =>
        location.name.toLowerCase().includes(locationFilter.toLowerCase())
      );
      filteredRestaurants = restaurants.filter((restaurant) =>
        filteredLocations.find((location) => location.id === restaurant.locationId)
      );
    }

    // Filter menu items based on the restaurant filter
    if (restaurantFilter) {
      filteredMenuItems = menuItems.filter((menuItem) =>
        menuItem.restaurantId === parseInt(restaurantFilter)
      );
      filteredRestaurants = restaurants.filter((restaurant) =>
        restaurant.id === parseInt(restaurantFilter)
      );
    }

    // Perform the necessary state updates
    setFilteredLocations(filteredLocations);
    setFilteredRestaurants(filteredRestaurants);
    setFilteredMenuItems(filteredMenuItems);
  };

  return (
    <div className="search-page">
      <div>
        <Header />
      </div>
      <div className="search-container">
        <div className="filter-container">
          <input
            className="search-input"
            type="text"
            placeholder="Search locations"
            value={locationFilter}
            onChange={(event) => setLocationFilter(event.target.value)}
          />
          <input
            className="search-input"
            type="text"
            placeholder="Search restaurants"
            value={restaurantFilter}
            onChange={(event) => setRestaurantFilter(event.target.value)}
          />
          <button className="search-button" onClick={handleSearch}>
            Search
          </button>
        </div>
        <div className="results-container">
          {filteredLocations.length > 0 && (
            <div className="location-results">
              <h2>Restaurants in {locationFilter}:</h2>
              <ul>
                {filteredRestaurants.map((restaurant) => (
                  <li key={restaurant.id}>{restaurant.name}</li>
                ))}
              </ul>
            </div>
          )}
          {filteredRestaurants.length > 0 && (
            <div className="restaurant-results">
              <h2>Menu Items in {restaurantFilter}:</h2>
              <ul>
                {filteredMenuItems.map((menuItem) => (
                  <li key={menuItem.id}>{menuItem.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Searchpage;
