import React, { useState } from 'react';

// Sample data for restaurants and menu items
const restaurants = [
  { name: 'Restaurant A', location: 'Location 1', menuItems: ['Item 1', 'Item 2', 'Item 3'] },
  { name: 'Restaurant B', location: 'Location 2', menuItems: ['Item 4', 'Item 5', 'Item 6'] },
  { name: 'Restaurant C', location: 'Location 1', menuItems: ['Item 7', 'Item 8', 'Item 9'] },
];

const App = () => {
  const [locationSearchTerm, setLocationSearchTerm] = useState('');
  const [restaurantSearchTerm, setRestaurantSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleLocationSearch = () => {
    const filteredRestaurants = restaurants.filter(
      (restaurant) => restaurant.location.toLowerCase() === locationSearchTerm.toLowerCase()
    );
    setSearchResults(filteredRestaurants);
  };

  const handleRestaurantSearch = () => {
    const filteredRestaurant = restaurants.find(
      (restaurant) => restaurant.name.toLowerCase() === restaurantSearchTerm.toLowerCase()
    );
    setSearchResults(filteredRestaurant ? [filteredRestaurant] : []);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Location"
          value={locationSearchTerm}
          onChange={(event) => setLocationSearchTerm(event.target.value)}
        />
        <button onClick={handleLocationSearch}>Search by Location</button>
      </div>
      <div>
        <input
          type="text"
          placeholder="Restaurant Name"
          value={restaurantSearchTerm}
          onChange={(event) => setRestaurantSearchTerm(event.target.value)}
        />
        <button onClick={handleRestaurantSearch}>Search by Restaurant</button>
      </div>
      <div>
        {searchResults.length === 0 ? (
          <p>No results found.</p>
        ) : (
          searchResults.map((restaurant) => (
            <div key={restaurant.name}>
              <h3>{restaurant.name}</h3>
              <p>Location: {restaurant.location}</p>
              <h4>Menu:</h4>
              <ul>
                {restaurant.menuItems.map((menuItem, index) => (
                  <li key={index}>{menuItem}</li>
                ))}
              </ul>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default App;
