import React, { useState } from "react";
import Header from "./Header";
import TopBrandRestaurants from "./TopBrandRestaurants";

const RestaurantSearch = () => {
  const [locationFilter, setLocationFilter] = useState("");
  const [restaurantFilter, setRestaurantFilter] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  // Sample restaurant data
  const restaurants = [
    {
      name: "Restaurant A",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          cuisine: "Cuisine A",
          price: "$10",
          image: "https://example.com/item1a.jpg"
        },
        {
          name: "Item 2A",
          cuisine: "Cuisine B",
          price: "$15",
          image: "https://example.com/item2a.jpg"
        },
        {
          name: "Item 3A",
          cuisine: "Cuisine C",
          price: "$12",
          image: "https://example.com/item3a.jpg"
        }
      ]
    },
    // Rest of the restaurant data...
  ];

  // Rest of the code...

  return (
    <div>
      <div>
        <Header />
      </div>
      {/* Location and restaurant search */}
      <div style={{ display: "flex", alignItems: "center" }}>
        <label>
          Location:
          <input type="text" value={locationFilter} onChange={handleLocationFilterChange} />
        </label>
        <label style={{ marginLeft: "10px" }}>
          Restaurant:
          <input type="text" value={restaurantFilter} onChange={handleRestaurantFilterChange} />
        </label>
        <button onClick={handleSearchClick}>Search</button>
      </div>

      {/* Display filtered restaurants or selected restaurant items */}
      {selectedRestaurant ? (
        <div className="selected-restaurant">
          {/* Rest of the code... */}
        </div>
      ) : (
        <div>
          {/* Display filtered restaurants */}
          {searchClicked ? (
            filteredRestaurants.length === 0 ? (
              <p>No restaurants found.</p>
            ) : (
              <ul>
                {filteredRestaurants.map((restaurant) => (
                  <li
                    key={restaurant.name}
                    onClick={() => handleRestaurantSelection(restaurant)}
                  >
                    <div>
                      <h3>{restaurant.name}</h3>
                      <img src={restaurant.image} alt={restaurant.name} style={{ width: "100px" }} />
                    </div>
                  </li>
                ))}
              </ul>
            )
          ) : (
            <TopBrandRestaurants />
          )}
        </div>
      )}
    </div>
  );
};

export default RestaurantSearch;
