import React, { useState } from 'react';
import Header from './Header';
import './Searchpage.css';

const Searchpage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // Dummy list of locations with additional information
  const locations = [
    { name: 'Chennai', options: ['Restaurant', 'Cafe', 'Hotel'] },
    { name: 'Coimbatore', options: ['Restaurant', 'Cafe', 'Hotel'] },
    { name: 'Bangalore', options: ['Restaurant', 'Cafe', 'Hotel'] },
    // Add more locations with options as needed
  ];

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    // Filter the locations based on the search query
    const filteredLocations = locations.filter((location) =>
      location.name.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(filteredLocations);
  };

  return (
    <div className="search-page">
      <div>
        <Header />
      </div>
      <div className="search-container">
        <input
          className="search-location"
          type="text"
          placeholder="Search locations"
          value={searchQuery}
          onChange={handleSearch}
        />
        {searchQuery && (
          <div className="suggestions-container">
            <ul className="suggestions">
              {searchResults.map((location) => (
                <li key={location.name}>
                  <span className="location-name">{location.name}</span>
                  <div className="options">
                    {location.options.map((option) => (
                      <span key={option} className="option">{option}</span>
                    ))}
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Searchpage;
