import React, { useState } from 'react';

// Sample data for restaurants and menu items
const restaurants = [
  { name: 'Restaurant A', location: 'Location 1', menuItems: ['Item 1', 'Item 2', 'Item 3'] },
  { name: 'Restaurant B', location: 'Location 2', menuItems: ['Item 4', 'Item 5', 'Item 6'] },
  { name: 'Restaurant C', location: 'Location 1', menuItems: ['Item 7', 'Item 8', 'Item 9'] },
];

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);

  const handleSearch = () => {
    const filteredRestaurants = restaurants.filter(
      (restaurant) => restaurant.location.toLowerCase() === searchTerm.toLowerCase()
    );

    if (filteredRestaurants.length === 1) {
      setSelectedRestaurant(filteredRestaurants[0]);
    } else {
      setSelectedRestaurant(null);
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Location"
          value={searchTerm}
          onChange={(event) => setSearchTerm(event.target.value)}
        />
        <button onClick={handleSearch}>Search by Location</button>
      </div>
      <div>
        {selectedRestaurant ? (
          <div>
            <h3>{selectedRestaurant.name}</h3>
            <p>Location: {selectedRestaurant.location}</p>
            <h4>Menu:</h4>
            <ul>
              {selectedRestaurant.menuItems.map((menuItem, index) => (
                <li key={index}>{menuItem}</li>
              ))}
            </ul>
          </div>
        ) : (
          restaurants.map((restaurant) => (
            <div key={restaurant.name}>
              <h3>{restaurant.name}</h3>
              <p>Location: {restaurant.location}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default App;
