import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Card } from 'react-bootstrap';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [images, setImages] = useState([]);

  const handleImageChange = (event) => {
    setSelectedImage(event.target.files[0]);
  };

  const handleImageUpload = () => {
    const formData = new FormData();
    formData.append('productImage', selectedImage);

    axios
      .post('http://localhost:8090/upload', formData)
      .then(() => {
        setSelectedImage(null);
        fetchImages();
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const fetchImages = () => {
    axios
      .get('http://localhost:8090/images')
      .then((response) => {
        setImages(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // Fetch images on initial component mount
  React.useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div className="container">
      <h1>Image Upload Form</h1>
      <Form>
        <Form.Group>
          <Form.Label>Select Image:</Form.Label>
          <Form.Control type="file" onChange={handleImageChange} />
        </Form.Group>
        <Button variant="primary" onClick={handleImageUpload} disabled={!selectedImage}>
          Upload
        </Button>
      </Form>
      <hr />
      <h2>Images</h2>
      <div className="image-list">
        {images.map((image) => (
          <Card style={{ width: '200px' }} key={image.id}>
            <Card.Img variant="top" src={`data:image/png;base64,${image.base64Data}`} />
            <Card.Body>
              <Card.Title>{image.name}</Card.Title>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
}

export default App;
