import axios from 'axios';

const BASE_URL = 'http://localhost:8090/menu-item'; // Update with your Spring Boot backend URL

export const addMenuItem = async (menuItem) => {
  try {
    const response = await axios.post(BASE_URL, menuItem);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const updateMenuItem = async (menuItem) => {
  try {
    const response = await axios.put(BASE_URL, menuItem);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const getAllMenuItems = async () => {
  try {
    const response = await axios.get(BASE_URL);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const getMenuItemById = async (id) => {
  try {
    const response = await axios.get(`${BASE_URL}/id?id=${id}`);
    return response.data;
  } catch (error) {
    throw error;
  }
};
