import React, { useState } from "react";

const RestaurantSearch = () => {
  const [locationFilter, setLocationFilter] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);

  // Sample restaurant data
  const restaurants = [
    { name: "Restaurant A", location: "Location 1", menuItems: ["Item 1A", "Item 2A", "Item 3A"] },
    { name: "Restaurant B", location: "Location 2", menuItems: ["Item 1B", "Item 2B", "Item 3B"] },
    { name: "Restaurant C", location: "Location 1", menuItems: ["Item 1C", "Item 2C", "Item 3C"] },
  ];

  // Handle location filter change
  const handleLocationFilterChange = (event) => {
    setLocationFilter(event.target.value);
  };

  // Handle search button click
  const handleSearchClick = () => {
    const filteredRestaurants = restaurants.filter((restaurant) => {
      return restaurant.location.toLowerCase().includes(locationFilter.toLowerCase());
    });
    setFilteredRestaurants(filteredRestaurants);
    setSelectedRestaurant(null); // Reset selected restaurant
  };

  // Handle restaurant selection
  const handleRestaurantSelection = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  return (
    <div>
      <h2>Restaurant Search</h2>

      {/* Location filter */}
      <label>
        Location:
        <input type="text" value={locationFilter} onChange={handleLocationFilterChange} />
      </label>

      {/* Search button */}
      <button onClick={handleSearchClick}>Search</button>

      {/* Display filtered restaurants */}
      <h3>Restaurants</h3>
      {filteredRestaurants.length === 0 ? (
        <p>No restaurants found.</p>
      ) : (
        <ul>
          {filteredRestaurants.map((restaurant) => (
            <li key={restaurant.name} onClick={() => handleRestaurantSelection(restaurant)}>
              {restaurant.name}
            </li>
          ))}
        </ul>
      )}

      {/* Display selected restaurant and menu items */}
      {selectedRestaurant && (
        <div>
          <h3>Selected Restaurant: {selectedRestaurant.name}</h3>
          <h4>Menu Items</h4>
          {selectedRestaurant.menuItems.length === 0 ? (
            <p>No menu items found.</p>
          ) : (
            <ul>
              {selectedRestaurant.menuItems.map((item) => (
                <li key={item}>{item}</li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default RestaurantSearch;
