import React, { useState } from "react";
import Header from "./Header";
import TopBrandRestaurants from "./TopBrandRestaurants";
import "./Searchpage.css";

const RestaurantSearch = () => {
  const [locationFilter, setLocationFilter] = useState("");
  const [restaurantFilter, setRestaurantFilter] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  const restaurants = [
    {
      name: "Restaurant A",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 8.99,
          cuisine: "Indian",
        },
        {
          name: "Item 2A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 9.99,
          cuisine: "Indian",
        },
        {
          name: "Item 3A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 7.99,
          cuisine: "Indian",
        },
      ],
    },
    {
      name: "Restaurant B",
      location: "Location 2",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 8.99,
          cuisine: "Indian",
        },
        {
          name: "Item 2A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 9.99,
          cuisine: "Indian",
        },
        {
          name: "Item 3A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 7.99,
          cuisine: "Indian",
        },
      ],
    },
    {
      name: "Restaurant C",
      location: "Location 1",
      image: "https://aabsweets.com/assets/img/logo-a2b.png",
      menuItems: [
        {
          name: "Item 1A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 8.99,
          cuisine: "Indian",
        },
        {
          name: "Item 2A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 9.99,
          cuisine: "Indian",
        },
        {
          name: "Item 3A",
          image: "https://media-cdn.tripadvisor.com/media/photo-s/12/d2/93/48/mini-tiffin.jpg",
          price: 7.99,
          cuisine: "Indian",
        },
      ],
    },
  ];

  const handleLocationFilterChange = (event) => {
    setLocationFilter(event.target.value);
  };

  const handleRestaurantFilterChange = (event) => {
    setRestaurantFilter(event.target.value);
  };

  const handleSearchClick = () => {
    const filteredRestaurants = restaurants.filter((restaurant) => {
      const locationMatch = restaurant.location
        .toLowerCase()
        .includes(locationFilter.toLowerCase());
      const restaurantMatch = restaurant.name
        .toLowerCase()
        .includes(restaurantFilter.toLowerCase());
      return locationMatch && restaurantMatch;
    });
    setFilteredRestaurants(filteredRestaurants);
    setSelectedRestaurant(null);
    setSearchClicked(true);
  };

  const handleRestaurantSelection = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  const renderMenuItem = (item) => (
    <div key={item.name} className="item-card">
      <h5>{item.name}</h5>
      <img src={item.image} alt={item.name} style={{ width: "100px" }} />
      <p>Cuisine: {item.cuisine}</p>
      <p>Price: ${item.price.toFixed(2)}</p>
      <button onClick={() => addToCart(item)}>Add to Cart</button>
    </div>
  );
  

  const renderMenuItems = (menuItems) => (
    <div>
      {menuItems.map(renderMenuItem)}
    </div>
  );

  const renderRestaurant = (restaurant) => (
    <div
      key={restaurant.name}
      className="restaurant-card"
      onClick={() => handleRestaurantSelection(restaurant)}
    >
      <img src={restaurant.image} alt={restaurant.name} style={{ width: "300px", height: "150px" }} />
      <div>
        <h3>{restaurant.name}</h3>
        <p>Delivery Time: {restaurant.deliveryTime}</p>
      </div>
    </div>
  );

  const renderRestaurants = (restaurantsToRender) => (
    <div className="restaurant-list">
      {restaurantsToRender.map(renderRestaurant)}
    </div>
  );

  let filteredRestaurantsContent = null;
  if (searchClicked) {
    if (selectedRestaurant) {
     
      const { name, image, menuItems } = selectedRestaurant;
      filteredRestaurantsContent = (
        <div className="selected-restaurant">
          <h3>Selected Restaurant: {name}</h3>
          <img src={image} alt={name} style={{ width: "200px" }} />
          <h4>Menu Items</h4>
          {menuItems.length === 0 ? (
            <p>No menu items found.</p>
          ) : (
            <div>
              <h4>Menu Items</h4>
              {renderMenuItems(menuItems)}
            </div>
          )}
        </div>
      );
    } else if (filteredRestaurants.length === 0) {
      filteredRestaurantsContent = <p>No restaurants found.</p>;
    } else {
      filteredRestaurantsContent = renderRestaurants(filteredRestaurants);
    }
  } else {
    filteredRestaurantsContent = <TopBrandRestaurants />;
  }

  return (
    <div>
      <div>
        <Header />
      </div>
      <div style={{ display: "flex", alignItems: "center" }}>
        <label>
          Location:
          <input type="text" value={locationFilter} onChange={handleLocationFilterChange} />
        </label>
        <label style={{ marginLeft: "10px" }}>
          Restaurant:
          <input type="text" value={restaurantFilter} onChange={handleRestaurantFilterChange} />
        </label>
        <button onClick={handleSearchClick}>Search</button>
      </div>
      {selectedRestaurant ? (
        <div className="selected-restaurant">
          <h3>Selected Restaurant: {selectedRestaurant.name}</h3>
          <img src={selectedRestaurant.image} alt={selectedRestaurant.name} style={{ width: "200px" }} />
          {selectedRestaurant.menuItems.length === 0 ? (
            <p>No menu items found.</p>
          ) : (
            <div>
              <h4>Menu Items</h4>
              {renderMenuItems(selectedRestaurant.menuItems)}
            </div>
          )}
        </div>
      ) : (
        <div>
          {filteredRestaurantsContent}
        </div>
      )}
    </div>
  );
};

export default RestaurantSearch;
