import React, { useState } from 'react';
import Header from './Header';
import TopBrandRestaurants from './TopBrandRestaurants';

// Sample data for restaurants and menu items
const restaurants = [
  { name: 'Restaurant A', location: 'Location 1', menuItems: ['Item 1', 'Item 2', 'Item 3'] },
  { name: 'Restaurant B', location: 'Location 2', menuItems: ['Item 4', 'Item 5', 'Item 6'] },
  { name: 'Restaurant C', location: 'Location 1', menuItems: ['Item 7', 'Item 8', 'Item 9'] },
];

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('location');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = () => {
    if (selectedFilter === 'location') {
      const filteredRestaurants = restaurants.filter(
        (restaurant) => restaurant.location.toLowerCase() === searchTerm.toLowerCase()
      );
      setSearchResults(filteredRestaurants);
    } else {
      const filteredRestaurant = restaurants.find(
        (restaurant) => restaurant.name.toLowerCase() === searchTerm.toLowerCase()
      );
      setSearchResults(filteredRestaurant ? [filteredRestaurant] : []);
    }
  };

  const handleFilterChange = (event) => {
    setSelectedFilter(event.target.value);
  };

  return (
    <div>
      <div>
        <Header />
      </div>
      <div>
        {searchResults.length === 0 ? (
          <TopBrandRestaurants />
        ) : (
          <div>
            <input
              type="text"
              value={searchTerm}
              onChange={(event) => setSearchTerm(event.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
            <div>
              <label>
                Filter by:
                <select value={selectedFilter} onChange={handleFilterChange}>
                  <option value="location">Location</option>
                  <option value="restaurant">Restaurant</option>
                </select>
              </label>
            </div>
            {searchResults.length === 0 ? (
              <p>No results found.</p>
            ) : (
              searchResults.map((restaurant) => (
                <div key={restaurant.name}>
                  <h3>{restaurant.name}</h3>
                  <p>Location: {restaurant.location}</p>
                  <h4>Menu:</h4>
                  <ul>
                    {restaurant.menuItems.map((menuItem, index) => (
                      <li key={index}>{menuItem}</li>
                    ))}
                  </ul>
                </div>
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
